import React from "react";

export default class Signup extends React.Component{
   constructor(props){
       super(props);
           this.state={
               Name:"",
               Password:"",
               Email:"",
               Phone:"",
               Profession:"",
           }
       }

    handleName = (event) => {
        this.setState({
            Name:event.target.value
        })
    }

    handlePassword = (event) => {
        this.setState({
            Password:event.target.value
        })
    }

    handleEmail = (event) => {
        this.setState({
            Email:event.target.value
        })
    }
    handlePhone = (event) => {
        this.setState({
            Phone:event.target.value
        })
    }
    handleProfession = (event) => {
        this.setState({
            Profession:event.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        const {Name,Password,Email,Phone,Profession} = this.state;
        localStorage.setItem("Name", Name );
        localStorage.setItem("Password", Password);
        localStorage.setItem("Email", Email);
        localStorage.setItem("Phone", Phone);
        localStorage.setItem("Profession", Profession);
        console.log(Name,Password,Email,Phone,Profession)       
        let token = localStorage.getItem("usertoken")
        console.log(token);
        if(!Name || !Password || !Email || !Phone || !Profession){
           window.alert("Please Fill all the Fields")
        } else {
            this.props.history.push(`/Login`)
        }
    }


    render(){
        return(
            <div className="container" style={{margin:40}}>
                <form>
                    <div class="form-group">
                        <label for="exampleInputEmail1">Name</label>
                        <input type="name" onChange={this.handleName} style={{width:500}} class="form-control" id="exampleInputEmail1"  placeholder="Name" />
                    </div>
                    <div class="form-group">
                        <label for="exampleInputPassword1">Password</label>
                        <input type="password" onChange={this.handlePassword} style={{width:500}} class="form-control" id="exampleInputPassword1" placeholder="Password" />
                    </div>
                    <div class="form-group">
                        <label for="exampleInputEmail1">Email</label>
                        <input type="email"  onChange={this.handleEmail} style={{width:500}} class="form-control" id="exampleInputEmail1" placeholder="Email" />
                    </div>
                    <div class="form-group">
                        <label for="exampleInputEmail1">Phone No.</label>
                        <input type="number" onChange={this.handlePhone} style={{width:500}} class="form-control" id="exampleInputEmail1"  placeholder="Enter Phone Number" />
                    </div>
                    <div class="form-group">
                        <label for="exampleFormControlSelect1">Profession</label>
                        <select class="form-control" onChange={this.handleProfession}  style={{width:500}}  id="exampleFormControlSelect1">
                        <option>Employee</option>
                        <option>Developer</option>
                        <option>Engineer</option>
                        <option>Accountant</option>
                        </select>
                    </div>
                    <button type="submit" onClick={this.handleSubmit} class="btn btn-primary">Submit</button>
                </form>
            </div>

        )
    }
}
